const links=document.querySelectorAll(".nav-link, .stay a, header a");links.forEach(link=>{link.addEventListener("click",function(e){e.preventDefault();});});const bodyHtml=document.querySelector("body, html");const nav=document.querySelector("nav .row");const header=document.querySelector("header");window.onload=function(){nav.classList.add("show");header.classList.add("show");}
const fade=document.querySelectorAll(".fade");window.addEventListener("scroll",()=>{for(let x in fade){if(bodyHtml.scrollTop>=fade[x].offsetTop-400){fade[x].classList.add("show");}}});const navLink=document.querySelectorAll(".nav-link");function smoothScroll(target,duration){var target=document.querySelector(target);var targetPosition=target.getBoundingClientRect().top;var startPosition=window.pageYOffset;var distance=targetPosition-startPosition;var startTime=null;function animation(currentTime){if(startTime===null)startTime=currentTime;var timeElapsed=currentTime-startTime;var run=ease(timeElapsed,startPosition,distance,duration);window.scrollTo(0,run);if(timeElapsed<duration)requestAnimationFrame(animation);}
function ease(t,b,c,d){t/=d/2;if(t<1)return c/2*t*t+b;t--;return-c/2*(t*(t-2)-1)+b;}
requestAnimationFrame(animation);}
navLink.forEach(iteem=>{iteem.addEventListener("click",function(){smoothScroll(this.dataset.scroll,2000);});});const stayElementLink=document.querySelector(".stay a");const headerElementLink=document.querySelector("header a");const stayPopup=document.getElementById("stay-popup");const stayPopupInfo=document.querySelector("#stay-popup .info");const stayPopupClose=document.querySelector("#stay-popup .close");function showPopup(){stayPopup.classList.add("show");stayPopupInfo.classList.add("show");bodyHtml.style.overflow="hidden";}
function hidePopup(){stayPopupInfo.classList.remove("show");stayPopup.classList.remove("show");bodyHtml.style.overflow="auto";}
stayElementLink.addEventListener("click",()=>{showPopup();});headerElementLink.addEventListener("click",()=>{showPopup();});stayPopupClose.addEventListener("click",()=>{hidePopup();});;window.addEventListener("keydown",(e)=>{if(e.keyCode===27){hidePopup();}});stayPopup.addEventListener("click",(e)=>{hidePopup();});stayPopupInfo.addEventListener("click",(e)=>{e.stopPropagation();});